import Axios from 'axios';
import React from 'react';
// import { Link } from 'react-router-dom';
import { BrowserRouter, Route, Router, Link, Switch } from 'react-router-dom';

export default class EditPage extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      full: '',
      short: this.props.short,
      editFull: '',
      errorMessage: ''
    };
  }

  componentDidMount() {
    Axios.get('http://localhost:5000/url/' + this.state.short).then(
      response => {
        this.setState({ full: response.data.full });
      }
    );
  }

  onChange(key, event) {
    this.setState({ [key]: event.target.value });
  }

  onSubmit() {
    Axios.put('http://localhost:5000/url/' + this.state.short + '/edit', {
      full: this.state.editFull,
      short: this.state.short
    })
      .then(function () {
        return Axios.get('http://localhost:5000/url/' + this.state.short);
      })
      .catch(err => {
        console.log(err.response.data);
        this.setState({ errorMessage: err.response.data });
      })
      .finally(() =>
        this.setState({
          urlFull: '',
          editFull: '',
          errorMessage: ''
        })
      );
  }

  onSubmitDelete() {
    Axios.delete('http://localhost:5000/url/' + this.state.short + '/edit', {
      short: this.state.short
    }).catch(err => {
      this.setState({ errorMessage: err.response.data });
    });
  }

  render() {
    return (
      <>
        <BrowserRouter>
          <Switch>
            <Route
              exact
              path={'/' + this.state.short + '/edit'}
              component={EditPage}
            >
              {this.state.errorMessage && (
                <h3 class='alert alert-danger' role='alert'>
                  {' '}
                  {this.state.errorMessage}{' '}
                </h3>
              )}
              <h1>{this.state.errorMessage} ieh ep</h1>
              <h1 class='text-center'>Edit This Url</h1>
              <div>
                <div>Full URL: {this.state.full}</div>
                <div>Shortened URL: {this.state.short}</div>
                <label for='test'>Edit Original URL:</label>
                <input
                  id='test'
                  value={this.state.editFull}
                  onChange={e => this.onChange('editFull', e)}
                  placeholder='Full URL'
                ></input>
                <span> </span>
                <button onClick={() => this.onSubmit()}>
                  submit
                  {/* <Link to={'/'}>Submit</Link> */}
                </button>
                <br></br>
                <button onClick={() => this.onSubmitDelete()}>
                  <Link to={'/'}>Delete This Url</Link>
                </button>
              </div>
            </Route>
          </Switch>
        </BrowserRouter>
      </>
    );
  }
}
