import React, { Component } from 'react';
import UrlService from '../services/UrlService';

export default class UpdateUrleComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      short: this.props.match.params.short,
      full: ''
    };
    this.changeFullHandler = this.changeFullHandler.bind(this);
    this.changeShortHandler = this.changeShortHandler.bind(this);
    this.updateUrl = this.updateUrl.bind(this);
  }

  componentDidMount() {
    UrlService.getUrlByPathId(this.state.short).then(res => {
      let url = res.data;
      this.setState({
        full: url.full,
        short: url.short
      });
    });
  }

  updateUrl = e => {
    e.preventDefault();
    let url = {
      full: this.state.full,
      short: this.state.short
    };
    console.log('url => ' + JSON.stringify(url));
    console.log('short => ' + JSON.stringify(this.state.short));
    UrlService.updateUrl(url, this.state.short).then(res => {
      this.props.history.push('/');
    });
  };

  changeFullHandler = event => {
    this.setState({ full: event.target.value });
  };

  changeShortHandler = event => {
    this.setState({ short: event.target.value });
  };

  cancel() {
    this.props.history.push('/urls');
  }

  render() {
    return (
      <div>
        <br></br>
        <div className='container'>
          <div className='row'>
            <div className='card col-md-6 offset-md-3 offset-md-3'>
              <h3 className='text-center'>Update URL</h3>
              <div className='card-body'>
                <form>
                  <div className='form-group'>
                    <label> Full URL: </label>
                    <input
                      placeholder='Full URL'
                      name='full'
                      className='form-control'
                      value={this.state.full}
                      onChange={this.changeFullHandler}
                    />
                  </div>

                  <button className='btn btn-success' onClick={this.updateUrl}>
                    Save
                  </button>
                  <button
                    className='btn btn-danger'
                    onClick={this.cancel.bind(this)}
                    style={{ marginLeft: '10px' }}
                  >
                    Cancel
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
