import Axios from 'axios';
import React from 'react';
import MainPage from './MainPage';
import { BrowserRouter, Route, Router, Link, Switch } from 'react-router-dom';

export default class EditPage extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      full: '',
      short: this.props.short,
      editFull: '',
      errorMessage: ''
    };
  }

  componentDidMount() {
    Axios.get('http://localhost:5000/url/' + this.state.short).then(
      response => {
        this.setState({ full: response.data.full });
      }
    );
  }

  onChange(key, event) {
    this.setState({ [key]: event.target.value });
  }

  onSubmit() {
    Axios.put('http://localhost:5000/url/' + this.state.short + '/edit', {
      full: this.state.editFull,
      short: this.state.short
    })
      .then(function () {
        return Axios.get('http://localhost:5000/url/' + this.state.short);
      })
      .then(response => {
        this.setState({ full: response.data.full });
      })
      .catch(err => {
        this.setState({ errorMessage: err.message });
      })
      .finally(() =>
        this.setState({
          urlFull: '',
          editFull: '',
          errorMessage: ''
        })
      );
  }

  onSubmitDelete() {
    Axios.delete('http://localhost:5000/url/' + this.state.short + '/edit', {
      short: this.state.short
    })
      // .then(function () {
      //   return Axios.get('http://localhost:5000/url/' + this.state.short);
      // })
      // .then(() => {
      //   this.setState({
      //     url: this.state.url.filter(item => item.full !== this.state.full)
      //   });
      // })
      .catch(err => {
        this.setState({ errorMessage: err.response.data });
      });
  }

  render() {
    return (
      <>
        <BrowserRouter>
          <Switch>
            <Route exact path='/{this.state.short}/edit' component={EditPage}>
              {this.state.errorMessage && (
                <h3 className='error'> {this.state.errorMessage} </h3>
              )}
              <h1>Edit This Url</h1>
              <div>
                <div>{this.state.full}</div>
                <div>{this.state.short}</div>
                <label for='test'>Edit Original URL:</label>
                <input
                  id='test'
                  value={this.state.editFull}
                  onChange={e => this.onChange('editFull', e)}
                ></input>
                <button onClick={() => this.onSubmit()}>
                  <Link to={'/'}>Submit</Link>
                </button>
                <button onClick={() => this.onSubmitDelete()}>
                  Delete This Url
                </button>
              </div>
            </Route>
            <Route
              path={'/' + this.state.urlEdit + '/edit'}
              component={EditPage}
            >
              <EditPage short={this.state.urlEdit} />
            </Route>
          </Switch>
        </BrowserRouter>
      </>
    );
  }
}
