import Axios from 'axios';
import React from 'react';

export default class App extends React.Component {
  // constructor(props) {
  //   super(props);
  //   Axios.get('http://localhost:5000/url').then(function (response) {
  //     console.log(response);
  //   });
  // }

  // render() {
  //   return (
  //     <>
  //       <h1>URL Shortener</h1>
  //       <h2>Shorten Your URL for FREE!</h2>
  //     </>
  //   );
  // }

  constructor(props) {
    super(props);

    this.state = {
      url: [],
      _id: '',
      full: '',
      short: 0
    };
  }

  componentDidMount() {
    Axios.get('http://localhost:5000/url').then(response => {
      this.setState({ url: response.data });
    });
  }

  onChange(key, event) {
    this.setState({ [key]: event.target.value });
  }

  onSubmit() {
    Axios.post('http://localhost:5000/url', {
      _id: this.state.urlId,
      full: this.state.urlFull,
      short: this.state.urlShort
    })
      .then(function () {
        return Axios.get('http://localhost:5000/url');
      })
      .then(response => {
        this.setState({ url: response.data });
      })
      .catch(error => console.log(error))
      .finally(() =>
        this.setState({
          urlId: '',
          urlFull: '',
          urlShort: ''
        })
      );
  }

  render() {
    return (
      <>
        <h1>URL Shortener</h1>
        <div>
          {this.state.url.map(item => (
            <div>
              <div>Orginal URL: {item.full}</div>
              <div>Shortened URL: {item.short}</div>
            </div>
          ))}
          <label for='full'>Orginal URL:</label>
          <input
            id='full'
            value={(this.state.urlFull, this.state.urlShort)}
            onChange={e => this.onChange('urlFull', e)}
          ></input>

          <label for='short'>Shortened URL:</label>
          <input
            id='short'
            value={(this.state.urlShort, this.state.urlFull)}
            onChange={e => this.onChange('urlShort', e)}
          ></input>
          <button onClick={() => this.onSubmit()}>Submit</button>
        </div>
      </>
    );
  }
}
