import React, { Component } from 'react';
import UrlService from '../services/UrlService';

export default class ListUrlComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      urls: []
    };
    this.addUrl = this.addUrl.bind(this);
    this.editUrl = this.editUrl.bind(this);
    this.deleteUrl = this.deleteUrl.bind(this);
  }

  deleteUrl(short) {
    UrlService.deleteUrl(short).then(res => {
      this.setState({
        urls: this.state.urls.filter(url => url.short !== short)
      });
    });
  }

  viewUrl(short) {
    this.props.history.push(`/${short}`);
  }

  editUrl(short) {
    this.props.history.push(`/${short}/edit`);
  }

  componentDidMount() {
    UrlService.getUrls().then(res => {
      this.setState({ urls: res.data });
    });
  }

  addUrl() {
    this.props.history.push('/add-url/_add');
  }

  render() {
    return (
      <div>
        <h2 className='text-center'>Urls List</h2>
        <div className='row'>
          <button className='btn btn-primary' onClick={this.addUrl}>
            {' '}
            Add Url
          </button>
        </div>
        <br></br>
        <div className='row'>
          <table className='table table-striped table-bordered'>
            <thead>
              <tr>
                <th> Original Url</th>
                <th> Shortened Url</th>
                <th> Actions</th>
              </tr>
            </thead>
            <tbody>
              {this.state.urls.map(url => (
                <tr key={url.short}>
                  <td> {url.full} </td>
                  <td> {url.short}</td>
                  <td>
                    <button
                      onClick={() => this.editUrl(url.short)}
                      className='btn btn-info'
                    >
                      Update{' '}
                    </button>
                    <button
                      style={{ marginLeft: '10px' }}
                      onClick={() => this.deleteUrl(url.short)}
                      className='btn btn-danger'
                    >
                      Delete{' '}
                    </button>
                    <button
                      style={{ marginLeft: '10px' }}
                      onClick={() => this.viewUrl(url.shor)}
                      className='btn btn-info'
                    >
                      View{' '}
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}
