import Axios from 'axios';
import React from 'react';

export default class EditPage extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      full: '',
      short: this.props.short,
      errorMessage: ''
    };
  }

  componentDidMount() {
    Axios.get('http://localhost:5000/url/' + this.state.short).then(
      response => {
        this.setState({ full: response.data.full });
      }
    );
  }

  onChange = event => {
    this.setState({ full: event.target.value });
  };

  onSubmit = event => {
    fetch('http://localhost:5000/url/' + this.state.short + '/edit', {
      method: 'put',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        full: this.state.full
      })
    })
      .then(response => response.json())
      .then(response => {
        this.setState({ full: response.data.full, short: response.data.short });
      })
      .catch(err => {
        this.setState({ errorMessage: err.message });
      })
      .finally(() =>
        this.setState({
          urlId: '',
          urlFull: '',
          urlShort: ''
        })
      );
  };
  // onSubmit() {
  //   Axios.put('http://localhost:5000/url/' + this.state.short + '/edit', {
  //     _id: this.state.urlId,
  //     full: this.state.urlFull,
  //     short: this.state.urlShort
  //   })
  //     .then(function () {
  //       return Axios.get('http://localhost:5000/url/' + this.state.short);
  //     })
  //     .then(response => {
  //       this.setState({ full: response.data.full, short: response.data.short });
  //     })
  //     .catch(err => {
  //       this.setState({ errorMessage: err.message });
  //     })
  //     .finally(() =>
  //       this.setState({
  //         urlId: '',
  //         urlFull: '',
  //         urlShort: ''
  //       })
  //     );
  // }

  render() {
    return (
      <>
        {this.state.errorMessage && (
          <h3 className='error'> {this.state.errorMessage} </h3>
        )}
        <h1>Edit This Url</h1>
        <div>
          <div>{this.state.full}</div>
          <div>{this.state.short}</div>
          <label for='full'>Edit Original URL:</label>
          <input
            id='full'
            value={this.state.urlFull}
            onChange={e => this.onChange('urlFull', e)}
          ></input>
          <button onClick={() => this.onSubmit()}>Submit</button>
        </div>
      </>
    );
  }
}
