import React, { Component } from 'react';
import UrlService from '../services/UrlService';

export default class CreateUrlComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      short: this.props.match.params.short,
      full: ''
    };
    this.changeFullHandler = this.changeFullHandler.bind(this);
    this.changeShortHandler = this.changeShortHandler.bind(this);
    this.saveOrUpdateUrl = this.saveOrUpdateUrl.bind(this);
  }

  componentDidMount() {
    if (this.state.short === '_add') {
      return;
    } else {
      UrlService.getUrlByPathId(this.state.short).then(res => {
        let url = res.data;
        this.setState({ full: url.full, short: url.short });
      });
    }
  }
  saveOrUpdateUrl = e => {
    e.preventDefault();
    let url = {
      full: this.state.full,
      short: this.state.short
    };
    console.log('url => ' + JSON.stringify(url));

    if (this.state.short === '_add') {
      UrlService.createUrl(url).then(res => {
        this.props.history.push('/urls');
      });
    } else {
      UrlService.updateUrl(url, this.state.short).then(res => {
        this.props.history.push('/urls');
      });
    }
  };

  changeFullHandler = event => {
    this.setState({ full: event.target.value });
  };

  changeShortHandler = event => {
    this.setState({ short: event.target.value });
  };

  cancel() {
    this.props.history.push('/urls');
  }

  getTitle() {
    if (this.state.id === '_add') {
      return <h3 className='text-center'>Add Url</h3>;
    } else {
      return <h3 className='text-center'>Update Url</h3>;
    }
  }
  render() {
    return (
      <div>
        <br></br>
        <div className='container'>
          <div className='row'>
            <div className='card col-md-6 offset-md-3 offset-md-3'>
              {this.getTitle()}
              <div className='card-body'>
                <form>
                  <div className='form-group'>
                    <label> Full Url: </label>
                    <input
                      placeholder='Full Url'
                      name='full'
                      className='form-control'
                      value={this.state.full}
                      onChange={this.changeFullHandler}
                    />
                  </div>
                  <div className='form-group'>
                    <label> Short Url: </label>
                    <input
                      placeholder='Short Url'
                      name='short'
                      className='form-control'
                      value={this.state.short}
                      onChange={this.changeShortHandler}
                    />
                  </div>

                  <button
                    className='btn btn-success'
                    onClick={this.saveOrUpdateUrl}
                  >
                    Save
                  </button>
                  <button
                    className='btn btn-danger'
                    onClick={this.cancel.bind(this)}
                    style={{ marginLeft: '10px' }}
                  >
                    Cancel
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
