import Axios from 'axios';
import React from 'react';
import EditPage from './EditPage';
import { BrowserRouter, Route, Router, Link, Switch } from 'react-router-dom';

export default class MainPage extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      url: [],
      _id: '',
      full: '',
      short: '',
      errorMessage: ''
    };
  }

  componentDidMount() {
    Axios.get('http://localhost:5000/url').then(response => {
      this.setState({ url: response.data });
    });
  }

  onChange(key, event) {
    this.setState({ [key]: event.target.value });
  }

  onSubmit() {
    Axios.post('http://localhost:5000/url', {
      _id: this.state.urlId,
      full: this.state.urlFull,
      short: this.state.urlShort
    })
      .then(function () {
        return Axios.get('http://localhost:5000/url');
      })
      .then(response => {
        this.setState({ url: response.data });
      })
      .catch(err => {
        this.setState({ errorMessage: err.message });
      })
      .finally(() =>
        this.setState({
          urlId: '',
          urlFull: '',
          urlShort: ''
        })
      );
  }

  onSubmitDelete() {
    Axios.delete('http://localhost:5000/url/' + this.state.short + '/edit', {
      full: this.state.full,
      short: this.state.short
    })
      .then(function () {
        return Axios.get('http://localhost:5000/url/' + this.state.short);
      })
      .then(response => {
        this.setState({
          urls: this.state.urls.filter(url => url.short !== short)
        });
      })
      .catch(err => {
        this.setState({ errorMessage: err.message });
      })
      .finally(() =>
        this.setState({
          urlFull: '',
          short: ''
        })
      );
  }

  render() {
    return (
      <>
        <BrowserRouter>
          <Switch>
            <Route exact path='/' component={MainPage}>
              {this.state.errorMessage && (
                <h3 className='error'> {this.state.errorMessage} </h3>
              )}
              <h1>URL Shortener</h1>
              <div>
                <table>
                  <tr>
                    <th>Orginal URL</th>
                    <th>Shortened URL</th>
                  </tr>
                  {this.state.url.map(item => (
                    <tr>
                      <td>{item.full}</td>
                      <td>
                        {item.short}
                        <button onClick={() => this.onSubmitDelete()}>
                          Delete This Url
                        </button>
                      </td>
                    </tr>
                  ))}
                </table>

                <label for='full'>Orginal URL:</label>
                <input
                  id='full'
                  value={this.state.urlFull}
                  onChange={e => this.onChange('urlFull', e)}
                ></input>

                <label for='short'>Shortened URL:</label>
                <input
                  id='short'
                  value={this.state.urlShort}
                  onChange={e => this.onChange('urlShort', e)}
                ></input>
                <button onClick={() => this.onSubmit()}>Submit</button>
                {/* <button>
                  <Link to={'/' + this.state.urlShort + '/edit'}>EditURL</Link>
                </button> */}
              </div>
              <div>
                <label for='edit'>Please entered the shortened URL:</label>
                <input
                  id='edit'
                  value={this.state.urlShort}
                  onChange={e => this.onChange('urlShort', e)}
                ></input>
                <button>
                  <Link to={'/' + this.state.urlShort + '/edit'}>EditURL</Link>
                </button>
              </div>
            </Route>
            <Route
              path={'/' + this.state.urlShort + '/edit'}
              component={EditPage}
            >
              <EditPage short={this.state.urlShort} />
            </Route>
          </Switch>
        </BrowserRouter>
      </>
    );
  }
}
